mapper.select:
select
from t_menu_center tmc, t_sys_menu tsm
tmc.t_sys_menu_id = tsm.id and tmc.is_del = 'N' and tsm.is_del = 'N'
and tmc.t_sys_group_id = #{groupId,jdbcType=VARCHAR}
and tsm.type = #{type,jdbcType=VARCHAR}
order by tsm.index, tsm.create_date

select
from t_sys_menu tsm
tsm.is_del = 'N'
and tsm.menu_name = #{menuName,jdbcType=VARCHAR}
and tsm.type = #{type,jdbcType=VARCHAR}
order by tsm.create_date desc
